import time, os, map
from Prologue import Dialogue

def anim(text, delay=0.02):
    for char in text:
        print(char, end='', flush=True)
        time.sleep(delay)
    print()

class Game:
    def reload(self):
        os.system("cls")

    def fullscreenprompt(self):
        print("\n\n")
        
        def terminalfullscreen():
            anim("                                                            I S   Y O U R   T E R M I N A L   C U R R E N T L Y   O N   F U L L   S C R E E N ?    \n")
            time.sleep(1)
            a = input("                                                           ---------------------------------------------------------------------------------------    \n\n                                                                          [1] Y E S                  |                  [2] N O                         \n")
            if a == '1':
                pass
            elif a == '2':
                os.system("cls")
                print("\n\n\n")
                print("                                            THIS GAME IS MEANT TO BE PLAYED IN FULL SCREEN ‚Äî drag the top of your terminal box to the very top of your display.     \n")
                terminalfullscreen()
            else:
                os.system("cls")
                time.sleep(1)
                print("\n\n\n\n\n                                                                     PLEASE ONLY PICK CHOICES [1] OR [2]! - [1] for YES, and [2] for NO.     \n")
                terminalfullscreen()
                
        terminalfullscreen()

