import time, sys, os

def anim(text, delay=0.0375):
    for char in text:
        print(char, end='', flush=True)
        time.sleep(delay)
    print()
x = "\n" * 10
print(x)


cache = ""

def changeLine(inp, index):
  global cache
  sp = cache.split(f"\n")
  sp[index] = inp
  cache = (f"\n").join(sp)
  
def reload():
  global cache
  os.system("cls")
  print(cache)

cache = f"\n\n\n"

reload()

