def fullscreenprompt():
    anim("\n\n")
    def terminalfullscreen():
        anim("                                                             IS   Y O U R   T E R M I N A L   C U R R E N T L Y   O N   F U L L   S C R E E N ?    \n")
        time.sleep(1)
        a = input("                                                           ---------------------------------------------------------------------------------------    \n\n                                                                          [1] Y E S                  |                  [2] N O                         \n")
        if a == '2':
            anim("\n\n\n\n\n                                            THIS GAME IS MEANT TO BE PLAYED IN FULL SCREEN â€” drag the top of your terminal box to the very top of your display.     ")
        elif a == '1':
            pass
        else:
            anim("\n\n\n\n\n                                                               PLEASE ONLY PICK CHOICES [1] OR [2]! - [1] for YES, and [2] for NO.     ")
            terminalfullscreen()
    terminalfullscreen()
fullscreenprompt()

